function [imgl,imgr]=genImages(img,RED_ind,GREEN_ind,BLUE_ind,FRAME,dcf)% Can select which eye gets which image here, in case you want to counterbalance across% subjects. e.g. cm{2-mod(subjNum,2)} for one eye, cm{1+mod(subjNum,2)} for other eyeimgl = img;imgl(find(img==0)) = RED_ind-1;imgl(find(img>0)) = GREEN_ind-1;if dcf	imgr = img;	imgr(find(img==0)) = GREEN_ind-1;	imgr(find(img>0)) = RED_ind-1;else	imgr=imgl;endif FRAME	imgl = AddFrameAndFix(imgl,BLUE_ind-1);	imgr = AddFrameAndFix(imgr,BLUE_ind-1); end