function session = initSession(paradigm)session = [];subjnum=getInput('Enter the subject number: ');sessnum = getInput('Enter the session number: ');if SubjSessExists(subjnum,sessnum)	error('Script aborted.');endsubjage=getInput('Enter the subject''s age: ');subjgend=getInput('Enter the subject''s gender: ','s',['m','f']);subjhand=getInput('Enter the subject''s handedness: ','s',['r','l']);% create session objectsession = struct('paradigm',paradigm,'datetime',datestr(now),...					'version',version,'subjnum',subjnum,'sessnum',sessnum,'params',[],...					'subject',struct('age',subjage,'gender',subjgend,'hand',subjhand),...					'trials',[],'notes','');                tmp = inputdlg('Notes','Notes',7);session.notes = tmp{1};